import React from "react";
import "./App.css";
import Header from "./components/Header";
import Container from "./components/Container";

function App() {
  function searchBooks(query) {
    let books = this.state.allBooks.filter(book => {
      return book.title.includes(query) || book.body.includes(query);
    });
    console.log(books);
  }

  return (
    <div className="App">
      <Header searchBooks={searchBooks} />
      <Container />
    </div>
  );
}

export default App;
