import React, { Component } from "react";

export default class Header extends Component {
  searchBar(e) {
    console.log(e.value);
  }

  render() {
    return (
      <header>
        <h1 id="header-title">Ubiqum Bookstore</h1>
        <input
          id="search-bar"
          onKeyUp={this.searchBar.bind(this)}
          type="textarea"
          placeholder="Search..."
        ></input>
      </header>
    );
  }
}

