import React, { Component } from "react";
import "./App.css";
import Header from "./components/Header";
import Book from "./components/Book";

export class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      books: []
    };
  }

  componentDidMount() {
    fetch("https://api.myjson.com/bins/zyv02")
      .then(res => {
        return res.json();
      })
      .then(result => {
        this.setState({ books: result.books });
      });
  }

  searchBar() {
    this.setState(term => {
      this.state.books.map(newBooks => {
        if (
          this.state.books.title.includes(term) ||
          this.state.books.description.includes(term)
        ) {
          return { newBooks };
        } else {
          return { books };
        }
      });
    });
  }

  render() {
    return (
      <div className="App">
        <Header />
        <div className="content-container">
          {this.state.books.map((book, index) => {
            return <Book key={index} book={book} />;
          })}
        </div>
      </div>
    );
  }
}
export default App;
